        {
            get { return _itemTypeText; }
            set { SetProperty(ref _itemTypeText, value); }
        }

        public override void ViewAppeared()
        {
            base.ViewAppeared();

            switch (_itemType)
            {
                case "donationType":
                    ItemTypeText = "Select Donation Type";
                    BindDonations();
                    break;

                case "cityType":
                    ItemTypeText = "Select City";
                    BindCities();
                    break;

                case "stateType":
                    ItemTypeText = "Select State";
                    BindStates();
                    break;

                case "bloodType":
                    ItemTypeText = "Select Blood Group";
                    BindBloodGroups();
                    break;

                default:
                    ItemTypeText = "Select Blood Group";
                    BindBloodGroups();
                    break;
            }        
        }

